calculations.useMemo:
const hazardData: Record<'low' | 'medium' | 'high' | 'critical', { days: number; waterPerDay: number; riskColor: string }> = {
  low: { days: 3, waterPerDay: 2, riskColor: 'green' },
  medium: { days: 5, waterPerDay: 3, riskColor: 'yellow' },
  high: { days: 7, waterPerDay: 4, riskColor: 'orange' },
  critical: { days: 10, waterPerDay: 5, riskColor: 'red' },
};

const riskLevel: 'low' | 'medium' | 'high' | 'critical' = 'low'; // 例

const calculations = useMemo(() => {
  const hazard = hazardData[riskLevel]; // 型安全にアクセス可能
  const locationInfo = locationRisk[location]; // locationRiskも型定義を確認

  const totalWaterNeeded = employeeCount * hazard.waterPerDay * hazard.days * locationInfo.multiplier;
  return { totalWaterNeeded, riskColor: hazard.riskColor };
}, [riskLevel, location, employeeCount]);
